package frag

import "testing"

// test primer creation ability
func Test_setPrimers(t *testing.T) {
	p := PCR{
		ID:  "testID",
		Seq: "",
	}

	err := p.SetPrimers()

	if err != nil {
		t.Error(err)
	}

	if p.Primers == nil || len(p.Primers) < 2 {
		t.Error("failed to set primers on fragment")
	}
}
